#include <FastLED.h>
#include <Arduino.h>
int NUM_LEDS=64;
int DATA_PIN=14;

CRGB leds[NUM_LEDS];
void setup()
{
    debug("setup");
    FastLED.addLeds(leds, NUM_LEDS);

}

int loopCount=0;

void loop()
{

    if (loopCount%2==0){
        leds[0] = FastLED.Red;
        leds[63] = FastLED.Black;
    }else{
        leds[0] = FastLED.Black;
        leds[63] = FastLED.Red;
    }
    debug("test"); 
    FastLED.show();
    loopCount++;

}
